import React,{useState, useEffect} from 'react'
import axios from 'axios';

function PredictComponent() {
    // Define el estado para los datos de entrada y la respuesta del servidor
    const [formData, setFormData] = useState({
      Tiempo_de_estudio: '',
      Asistencia: '',
    });
  
    const [prediction, setPrediction] = useState(null);
  
    // Función para manejar los cambios en los campos de entrada
    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    // Función para enviar la solicitud POST al servidor
    const handlePredict = () => {
      axios.post('/predict', formData)
        .then((response) => {
          // Maneja la respuesta del servidor
          setPrediction(response.data.Prediccion);
        })
        .catch((error) => {
          // Maneja los errores de la solicitud
          console.error('Error:', error);
        });
    };
  
    return (
      <div>
        <h2>Predicción</h2>
        <label>Tiempo de Estudio:</label>
        <input
          type="number"
          name="Tiempo_de_estudio"
          value={formData.Tiempo_de_estudio}
          onChange={handleInputChange}
        />
        <br />
        <label>Asistencia:</label>
        <input
          type="number"
          name="Asistencia"
          value={formData.Asistencia}
          onChange={handleInputChange}
        />
        <br />
        <button onClick={handlePredict}>Predecir</button>
        {prediction !== null && (
          <p>Resultado de la predicción: {prediction}</p>
        )}
      </div>
    );
  }
  
  export default PredictComponent;
